import React, { useState } from "react";

const FriendRequests: React.FC = () => {
    const [requests, setRequests] = useState([
        { id: 1, username: "PlayerOne" },
        { id: 2, username: "GamerDude" },
        { id: 3, username: "VictoryQueen" }
    ]);

    const handleAccept = (id: number) => {
        setRequests(requests.filter(request => request.id !== id));
        // Add backend API call here to accept request
    };

    const handleDecline = (id: number) => {
        setRequests(requests.filter(request => request.id !== id));
        // Add backend API call here to decline request
    };

    return (
        <div className="friends-page">
            <div className="friends-container">
                <div className="friends-header">
                    <span className="menu-icon">â˜°</span>
                    <span>Friends - Friend Requests</span>
                    <button className="back-button" onClick={() => window.location.href = "#/friends"}>
                        ðŸ”™
                    </button>
                </div>
                <div className="requests-list">
                    <h2>Pending Friend Requests</h2>
                    {requests.length > 0 ? (
                        requests.map(request => (
                            <div key={request.id} className="request-card">
                                <span>{request.username}</span>
                                <div className="request-actions">
                                    <button className="accept-button" onClick={() => handleAccept(request.id)}>âœ” Accept</button>
                                    <button className="decline-button" onClick={() => handleDecline(request.id)}>âœ– Decline</button>
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>No new friend requests.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default FriendRequests;
