import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Image, Button, Form, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const EditProfile = () => {
  const navigate = useNavigate();

  const [username, setUsername] = useState(""); 
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState(""); 
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_API_URL}backend/getProfile.php`, {
          method: "GET",
          credentials: "include",
        });
        const result = await response.json();
  
        console.log("API Response:", result);
  
        if (result.status === "success" && result.data) {
          setUsername(result.data.username);
          setEmail(result.data.email);
        } else {
          setMessage("Failed to load profile.");
          setIsError(true);
        }
      } catch (error) {
        setMessage("An error occurred while fetching profile.");
        setIsError(true);
        console.error("Error fetching profile:", error);
      }
    };
  
    fetchProfile();
  }, []);

  const handleBackButton = () => {
    navigate('/userProfile');
  };

  const handleSaveProfile = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}backend/updateProfile.php`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, email }),
        credentials: "include",
      });
      const result = await response.json();

      if (result.status === "success") {
        setMessage("Profile updated successfully!");
        setIsError(false);
      } else {
        setMessage(result.message || "Failed to update profile.");
        setIsError(true);
      }
    } catch (error) {
      setMessage("An error occurred while updating the profile.");
      setIsError(true);
      console.error("Error updating profile:", error);
    }
  };

  return (
    <Container className="user-edit-profile-container py-5">
      <button className="user-edit-back-button btn btn-light btn-lg fs-2 p-10" aria-label="Go back" onClick={handleBackButton}>‚Üê</button>
      <Row className="user-edit-row justify-content-center">
        <Col md={8} lg={6} className="user-edit-col">
          <h1 className="user-edit-title display-4 fw-bold">Edit Profile</h1>
          <Image
            src="./static/ProfilePlaceholder.png"
            roundedCircle
            width={200}
            height={200}
            className="user-edit-profile-image d-block mx-auto mb-4"
            alt="User profile"
          />
          <Form.Group className="user-edit-form-group mb-4">
            <Form.Label className="user-edit-label h4">User Name</Form.Label>
            <Form.Control
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="user-edit-input font-weight-bold"
              style={{ backgroundColor: '#ededed', padding: '23px 12px' }}
            />
          </Form.Group>
          <Form.Group className="user-edit-form-group mb-4">
            <Form.Label className="user-edit-label h4">Email Address</Form.Label>
            <Form.Control
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="user-edit-input text-decoration-underline"
              style={{ backgroundColor: '#ededed', padding: '23px 12px' }}
            />
          </Form.Group>

          {message && (
            <Alert className="user-edit-alert" variant={isError ? "danger" : "success"}>{message}</Alert>
          )}

          <div className="user-edit-buttons d-flex justify-content-between mt-5">
            <Button className="user-edit-cancel-button" variant="outline-secondary" size="lg" onClick={handleBackButton}>Cancel</Button>
            <Button className="user-edit-save-button" variant="success" size="lg" onClick={handleSaveProfile}>Save Profile</Button>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default EditProfile;
