import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Image } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

interface EditProfilePageProps {
  initialUserName: string;
  initialEmail: string;
  onSave: (userName: string, email: string) => void;
  onCancel: () => void;
}

const MobileEditProfile: React.FC<EditProfilePageProps> = ({
  initialUserName,
  initialEmail,
  onSave,
  onCancel
}) => {
  const [userName, setUserName] = useState(initialUserName);
  const [email, setEmail] = useState(initialEmail);

  const handleSave = () => {
    console.log('What ?')
  };

  return (
    <Container className="py-5">
      <Row className="justify-content-center">
        <Col xs={12} md={8} lg={6}>
          <h1 className="mb-4">Edit Profile</h1>
          <Form>
            <Form.Group className="mb-4 text-center">
              <Image
                src="https://cdn.builder.io/api/v1/image/assets/1749da0ea8b2432f926a2c3deb56fd0c/5eb7f22987c02f80265245732f408d1d436123b072254206c7423d2c943cb14c?apiKey=1749da0ea8b2432f926a2c3deb56fd0c&"
                alt="User profile"
                roundedCircle
                width={100}
                height={100}
                className="mb-3"
              />
              <div>
                <Button variant="secondary" size="sm">
                  Change Picture
                </Button>
              </div>
            </Form.Group>
            <Form.Group className="mb-4" controlId="userName">
              <Form.Label>User Name</Form.Label>
              <Form.Control
                type="text"
                value={userName}
                onChange={(e) => setUserName(e.target.value)}
                aria-label="User Name"
              />
            </Form.Group>
            <Form.Group className="mb-4" controlId="email">
              <Form.Label>Email Address</Form.Label>
              <Form.Control
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                aria-label="Email Address"
              />
            </Form.Group>
            <div className="d-flex justify-content-end mt-4">
              <Button variant="link" onClick={onCancel} className="me-3">
                Cancel
              </Button>
              <Button variant="success" onClick={handleSave}>
                Save Profile
              </Button>
            </div>
          </Form>
        </Col>
      </Row>
    </Container>
  );
};

export default MobileEditProfile;
