import React from "react";
// import "./register.css";

const SettingsPrivacy: React.FC = () => {

    const [username, setUsername] = React.useState("");
    const [email, setEmail] = React.useState("");
    const [password, setPassword] = React.useState("");
    const [confirm_password, setconfirm_password] = React.useState("");
    const [error, setError] = React.useState("");
    const [delete_account, setDelete_account] = React.useState("");

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const data = {username, email, password, confirm_password};
        console.log(data);

        const response = await fetch("http://localhost/register", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(data),
        })

        const result = await response.json();
        console.log(result);
        console.log(result["status"]);

        if (result["status"] === "success") {
            window.location.href = "/login";
        }
        else {
            setError(result["message"]);
        }

    };
        

  return (
    <div className="auth-container">
        <div className="login-box">
            <h2>Account Deletion</h2>
            <h4>To delete your account please enter all the information corectly</h4>
            <form onSubmit={handleSubmit}>
                <label>Username</label>
                <input type="text" placeholder="Enter your username" value={username} onChange={(e) => setUsername(e.target.value)}/>

                <label>Email</label>
                <input type="email" placeholder="Enter your email" value={email} onChange={(e) => setEmail(e.target.value)} />

                <label>Password</label>
                <input type="password" placeholder="Create a password" value={password} onChange={(e) => setPassword(e.target.value)} />

                <label>Confirm Password</label>
                <input type="password" placeholder="Reenter your password" value={confirm_password} onChange={(e) => setconfirm_password(e.target.value)} />

                <label>Please Type "Confirm"</label>
                <input type="text" value={delete_account} onChange={(e) => setDelete_account(e.target.value)} />
                <button type="submit" >Register</button>
                {error && <p className="error-message">{error}</p>}
            </form>
        </div>
    </div>
  );
};

export default SettingsPrivacy;
