import React from "react";
import "./explore.css";
import { useNavigate } from "react-router-dom";
import Sidebar from "../user_profile/Sidebar";

const genres = [
  { name: "Rock", color: "#A44036" },
  { name: "Pop", color: "#E91E63" },
  { name: "Kpop", color: "#9C27B0" },
  { name: "Country", color: "#2196F3" },
  { name: "Classical", color: "#FFC107" },
  { name: "Introspective", color: "#4CAF50" },
  { name: "Electronic", color: "#00BCD4" },
];

const artists = ["Artist 1", "Artist 2", "Artist 3", "Artist 4", "Artist 5"];

const Explore: React.FC = () => {
  const navigate = useNavigate();

  const handleGenreClick = (genre: string) => {
    navigate(`/genre/${genre.toLowerCase()}`);
  };

  const handleArtistClick = (artist: string) => {
    navigate(`/artist/${artist.toLowerCase().replace(" ", "-")}`);
  };

  return (
    <div className="explore-page">
      <Sidebar />
      <div className="explore-content">
        {/* Search Bar */}
        <div className="search-bar-container">
          <input
            type="text"
            className="search-bar"
            placeholder="Search for an album, genre, artist, songs... 🔍"
          />
        </div>

        {/* Genre Section */}
        <h2 className="section-title">Browse All</h2>
        <div className="genre-container">
          {genres.map((genre) => (
            <button
              key={genre.name}
              className="genre-box"
              style={{ backgroundColor: genre.color }}
              onClick={() => handleGenreClick(genre.name)}
            >
              {genre.name}
            </button>
          ))}
        </div>

        {/* Top Artists Section */}
        <h2 className="section-title">Top Artists</h2>
        <div className="artist-container">
          {artists.map((artist) => (
            <button
              key={artist}
              className="artist-button"
              onClick={() => handleArtistClick(artist)}
            >
              {artist}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Explore;
