import { useState, useEffect } from "react";

const FriendRequests = () => {
    const [pendingFriends, setPendingFriends] = useState<string[]>([]);
    const [error, setError] = useState<string | null>(null);
    const [isLoading, setIsLoading] = useState(false);

    const fetchFriendRequests = async () => {
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}backend/getFriendRequests.php`, {
                method: "GET",
                credentials: "include",
                headers: { "Content-Type": "application/json" },
            });
            
            const result = await response.json();
            if (result.status === "success") {
                setPendingFriends(result.pendingFriends || []);
            } else {
                setError(result.message);
            }
        } catch (err) {
            setError("Failed to fetch friend requests.");
            console.error("⚠️ Error:", err);
        }
    };

    useEffect(() => {
        fetchFriendRequests();
    }, []);

    const acceptFriendRequest = async (friendUsername: string) => {
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}backend/acceptFriends.php`, {
                method: "POST",
                credentials: "include",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ friend: friendUsername }),
            });

            const result = await response.json();
            if (result.status === "success") {
                setPendingFriends(pendingFriends.filter(name => name !== friendUsername));
            } else {
                console.error("Error accepting request:", result.message);
            }
        } catch (err) {
            console.error("⚠️ Network error:", err);
        }
    };

    return (
        <div className="friend-requests-container">
            <h2>Pending Friend Requests</h2>
            {error && <p className="error-message">{error}</p>}
            {pendingFriends.length > 0 ? (
                <ul>
                    {pendingFriends.map((friend, index) => (
                        <li key={index} className="friend-request-item">
                            <span className="friend-name">{friend}</span>
                            <button className="accept-button" onClick={() => acceptFriendRequest(friend)}>✅ Accept</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p className="no-requests">No pending friend requests.</p>
            )}
        </div>
    );
};

export default FriendRequests;
