import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const CommunityPage = () => {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState("");
  const [myCommunities, setMyCommunities] = useState(["Rock Lovers", "Jazz Enthusiasts", "Indie Vibes"]);
  const [recommendedCommunities, setRecommendedCommunities] = useState(["Hip-Hop Heads", "Classical Masters", "EDM Nation"]);

  const handleBackButton = () => {
    navigate("/settings");
  };

  const handleJoinCommunity = (community: string) => {
    if (!myCommunities.includes(community)) {
      setMyCommunities([...myCommunities, community]);
    }
  };

  return (
    <div className="community-page p-4 max-w-lg mx-auto">
      <button className="mb-4 p-2 bg-gray-200 rounded" onClick={handleBackButton}>Go Back</button>
      
      <h2 className="text-xl font-bold mb-2">My Communities</h2>
      <ul className="mb-4">
        {myCommunities.map((community, index) => (
          <li key={index} className="p-2 border-b">{community}</li>
        ))}
      </ul>
      
      <button 
        className="mb-4 p-2 bg-blue-500 text-white rounded"
        onClick={() => document.getElementById("search-bar")?.focus()}
      >
        Add Music Communities
      </button>
      
      <input
        id="search-bar"
        type="text"
        placeholder="Search for communities..."
        className="p-2 border rounded w-full mb-4"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      
      <h2 className="text-xl font-bold mb-2">Recommended Communities</h2>
      <ul>
        {recommendedCommunities.map((community, index) => (
          <li key={index} className="p-2 border-b flex justify-between">
            {community}
            <button 
              className="ml-2 p-1 bg-green-500 text-white rounded"
              onClick={() => handleJoinCommunity(community)}
            >
              Join
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CommunityPage;
