import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./feed.css";

const Feed = () => {
    const posts = [
        { "title": "Post 1", "description": "This is the first post", "media": "https://www.w3schools.com/html/mov_bbb.mp4", "song": "Song 1" },
        { "title": "Post 2", "description": "This is the second post", "media": "https://www.w3schools.com/html/movie.mp4", "song": "Song 2" },
    ];

    const [currentPost, setCurrentPost] = useState(0);
    const navigate = useNavigate();

    const handleSwipe = (e: React.WheelEvent) => {
        if (e.deltaY > 0 && currentPost < posts.length - 1) {
            setCurrentPost(currentPost + 1);
        } else if (e.deltaY < 0 && currentPost > 0) {
            setCurrentPost(currentPost - 1);
        }
    };

    return (
        <div className="feed-container" onWheel={handleSwipe}>
            <div className="post">
                <video className="post-video" src={posts[currentPost].media} autoPlay loop muted></video>
                <div className="post-info">
                    <h2>{posts[currentPost].title}</h2>
                    <p>{posts[currentPost].description}</p>
                    <p className="song">ðŸŽµ {posts[currentPost].song}</p>
                </div>
            </div>
        </div>
    );
};

export default Feed;
